mapper.select:
SELECT st.TASK_ID, st.TASK_NAME, st.SUB_ID, st.DESCRIPTION, st.TEACHER_ID, u1.REAL_NAME AS TEACHER_NAME, st.STU_ID, u2.REAL_NAME AS STUDENT_NAME, st.START_TIME, st.END_TIME, f.DOC_ID, f.FILE_ID, f.FILE_NAME FROM stage_task st JOIN teacher t ON ( t.TEACHER_ID = st.TEACHER_ID ) JOIN USER u1 ON ( u1.USER_ID = t.USER_ID ) JOIN student s ON ( s.STU_ID = st.STU_ID ) JOIN USER u2 ON ( u2.USER_ID = s.USER_ID ) LEFT JOIN file_storage f ON ( f.DOC_ID = st.DOC_ID) WHERE t.TEACHER_ID=#{teacherId}
AND st.SUB_ID=1
AND st.STU_ID=1
