mapper.update:
UPDATE subject
SET STATUS=#{status},
    FEEDBACK=#{feedback}
WHERE SUB_ID = #{subId}

mapper.select:
SELECT s.SUB_ID , s.SUB_NAME, s.DOC_ID, s.REQUIREMENT, s.DESCRIPTION, s.ZONE, u.REAL_NAME AS TEACHER_NAME, s.POSE_TIME, t.HOME_PAGE, t.TITLE, c.COLLEGE_NAME, s.CAPACITY, s.CHOSEN, f.FILE_NAME, f.FILE_ID FROM `subject` s JOIN teacher t ON (t.TEACHER_ID = s.POSE_BY) JOIN college c ON (c.COLLEGE_ID = t.COLLEGE_ID) JOIN USER u ON (t.USER_ID = u.USER_ID) LEFT JOIN file_storage f ON (s.DOC_ID = f.DOC_ID) WHERE s.`STATUS` = 'YTG'
AND u.REAL_NAME=#{subject.teacherName}
AND s.ZONE = #{subject.zone}
AND (s.SUB_NAME LIKE CONCAT('%',#{subject.keyWord},'%') OR s.DESCRIPTION LIKE CONCAT('%',#{subject.keyWord},'%'))

SELECT DISTINCT s.SUB_ID , s.SUB_NAME, s.REQUIREMENT, s.DESCRIPTION, s.ZONE, s.STATUS, s.DOC_ID, u.REAL_NAME AS TEACHER_NAME, u.TEL, u.MAIL, s.POSE_TIME, t.HOME_PAGE, t.TITLE, c.COLLEGE_NAME, s.CAPACITY, s.CHOSEN, f.FILE_NAME, f.FILE_ID FROM `subject` s JOIN teacher t ON (t.TEACHER_ID = s.POSE_BY) JOIN college c ON (c.COLLEGE_ID = t.COLLEGE_ID) JOIN USER u ON (t.USER_ID = u.USER_ID) LEFT JOIN file_storage f ON (s.DOC_ID = f.DOC_ID) WHERE 1=1
AND u.REAL_NAME=#{subject.teacherName}
AND s.ZONE = #{subject.zone}
AND (s.SUB_NAME LIKE CONCAT('%',#{subject.keyWord},'%') OR s.DESCRIPTION LIKE CONCAT('%',#{subject.keyWord},'%'))
